<?php/* This file contains an object that create connection to the Server, creates the dataBase, creates tables and inseres them the Initial values.*/class createConnection {	var $con;	function create(){		//Create conncetion		$con = mysql_connect("localhost","root" ,"");		// Check connection		if (!$con)		{			return false;		}		else{			if($this->createDB()){				mysql_select_db('my_db1');				return true;			}			else{				return false;			}		}	}	// Create database	function createDB(){		$sql="CREATE DATABASE IF NOT EXISTS my_db1";		return(mysql_query($sql));	}	//Drop the old table	function dropTable(){		//Select the db		$sql="DROP TABLE IF EXISTS datatable";			return (mysql_query($sql));		}			//Create a new table.	function creatTable($sql){		return(mysql_query($sql));	}			  //Inserts to the DB from a json file.  function insertToDB(){		$strin = file_get_contents ("data/items.json");		$result = json_decode($strin, true);		foreach($result as $value) {			if($value) {					$id = $value['id'];					$title = $value['title'];					$company = $value['company'];					$image = $value['image'];					$price = $value['price'];				mysql_query("INSERT INTO datatable (id, title, comapny, image,price) VALUES ('$id','$title', '$company', '$image', '$price');");			}		}	}	}?>