<?php/* This file loads all the data from the server and converts it to json format.*/include "DataBaseObject.php";//Creates the connection$cone= new createConnection();$cone->create();  $sql = "select * from datatable"; $result = mysql_query($sql);$json = array();//Goes over all the data and converts it.if(mysql_num_rows($result)){	while($row=mysql_fetch_row($result)){		$item = array('id' => $row[0], 'title' => $row[1], 'company' => $row[2], 'image' => $row[3], 'price' => $row[4],);		$json[]= $item;	}echo json_encode($json);}?>